import { Component } from '@angular/core';
import { MenuItem } from 'primeng/api';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'test-competences';
  menus: MenuItem[] = [
    {
      label: "Accueil",
      icon: "pi pi-home",
      routerLink:'accueil',
      command: () =>{
        this.active = 0
      }
    },
    {
      label: "Entité",
      icon: "pi pi-briefcase",
      routerLink:'entites',
      command: () =>{
        this.active = 1
      }
    },
    {
      label: "Balance",
      icon: "pi pi-chart-bar",
      routerLink:'balances',
      command: () =>{
        this.active = 2
      }
    },
    {
      label: "Notes annexes",
      icon: "pi pi-home",
      routerLink:'annexes',
      command: () =>{
        this.active = 3
      }
    },
    {
      label: "Etats financiers",
      icon: "pi pi-folder",
      routerLink:'financier',
      command: () =>{
        this.active = 4
      }
    },
    {
      label: "Plan comptable",
      icon: "pi-list",
      routerLink:'plan-comptable',
      command: () =>{
        this.active = 5
      }
    }
  ]
}
