import { Component } from '@angular/core';
import { MenuItem } from 'primeng/api';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'test-competences';
  active = 1
  menus: MenuItem[] = [
    {
      label: "АИ-92",
    },
    {
      label: "КЕРОСИН",
    },
    {
      label: "ДТ ЗИМНЕЕ",
    },
    {
      label: "ТС-1",
    },
    {
      label: "АИ-95",
    },
    {
      label: "МАЗУТ",
    },
    {
      label: "ГАЗОЙЛЬ",      
    },
    {
      label: "НЕФТЬ",      
    }
  ]
  reservoirs = [
    {
      name: "Macca",
      numb: "980",
      percent: 98,
      sentence: "Уровень",
      sentence1: "керосина",
      sentence2: "в резервуаре №1",
      color: "#EB5757"
    }
  ]
}
